create table orders (total decimal(12,2), created_date datetime(6), customer_id bigint, employee_id bigint, id bigint not null auto_increment, status enum ('CANCELED','PAID','PENDING'), primary key (id)) engine=InnoDB;
create table payments (amount decimal(12,2) not null, id integer not null auto_increment, customer_id bigint, order_id bigint, payment_date datetime(6), payment_method varchar(50), note varchar(500), primary key (id)) engine=InnoDB;
create table products (buy_price decimal(12,2), sell_price decimal(12,2), stock integer, category_id bigint, id bigint not null auto_increment, code varchar(50) not null, name varchar(100) not null, unit varchar(255), primary key (id)) engine=InnoDB;
create table refresh_tokens (revoked bit not null, expiry_date datetime(6) not null, id bigint not null auto_increment, user_id bigint, token varchar(255) not null, primary key (id)) engine=InnoDB;
create table roles (id bigint not null auto_increment, name varchar(50) not null, primary key (id)) engine=InnoDB;
create table salaries (basic_salary decimal(12,2), bonus decimal(12,2), month date, employee_id bigint, id bigint not null auto_increment, primary key (id)) engine=InnoDB;
create table users (employee_id bigint, id bigint not null auto_increment, role_id bigint, username varchar(50) not null, email varchar(255) not null, password varchar(255) not null, primary key (id)) engine=InnoDB;
